INSERT [eml].[n_s_email_templates] ([template_id], [subject], [body], [is_body_html], [created_by], [created_on], [updated_by], [updated_on]) VALUES (1, N'ПЕАУ - Активиране на профил в портала за електронни административни услуги на МВР', N'Здравейте,<br /><br />Вие направихте успешна регистрация в Портала за електронни административни услуги на Министерство на вътрешните работи.<br />Адресът на електронна поща за Вашия потребителски профил е {EMAIL}.<br /><br />Необходимо е да активирате профила си, преди да го използвате за влизане в портала. За тази цел изберете хипервръзката:<br /><br /><a href="{ACTIVATION_LINK}">{ACTIVATION_LINK}</a><br /><br />Ако при избор на хипервръзката страницата не се зарежда, моля, копирайте уеб адреса във Вашия интернет браузър.<br />Срокът за активиране на профила изтича на {DEADLINE}. След изтичането му можете да заявите изпращане на ново съобщение с нов срок за активиране, като изберете дадената по-горе хипервръзка.<br /><br />За да откажете направената регистрация, моля кликнете <a href="{CANCEL_LINK}">тук</a>.<br />Това съобщение е автоматично, моля не отговаряйте! <br /><br />С уважение, <br />Екипът на Министерство на вътрешните работи.', 1, 1, CAST(N'2020-06-11T10:17:38.5150000+03:00' AS DateTimeOffset), 1, CAST(N'2020-06-11T10:17:38.5150000+03:00' AS DateTimeOffset))
GO
INSERT [eml].[n_s_email_templates] ([template_id], [subject], [body], [is_body_html], [created_by], [created_on], [updated_by], [updated_on]) VALUES (2, N'ПЕАУ - Нова парола за портала за електронни административни услуги на МВР', N'Здравейте,<br /><br />Вие заявихте "Забравена парола" за достъп до Портала за електронни административни услуги на Министерство на вътрешните работи.<br />За да промените паролата си за достъп, е необходимо да последвате дадения по-долу линк:<br /><br /><a href="{ACTIVATION_LINK}">{ACTIVATION_LINK}</a><br /><br />Ако при избор на линка, страницата не се зарежда, моля копирайте същия във Вашия интернет браузер. Ако смяната не бъде извършена до {DEADLINE}, линкът ще бъде автоматично деактивиран.<br /><br />Това съобщение е автоматично, моля не отговаряйте! Ако имате допълнителни въпроси, моля да се свържете с нас на телефон 0700 121 07.<br /><br />С уважение, <br />Екипът на Министерство на вътрешните работи', 1, 1, CAST(N'2020-06-12T11:36:30.9610000+03:00' AS DateTimeOffset), 1, CAST(N'2020-06-12T11:36:30.9610000+03:00' AS DateTimeOffset))
GO